.remove(port); System.out.println("å®¢æˆ·ç«¯ [" + port + "]å·²ç»æ–­å¼€è¿æ¥"); } } // è½¬å‘æ¶ˆæ¯ public synchronized void forwardMSG(Socket socket, String msg) throws IOException { // è½¬å‘ç»™é™¤å»å‘é€è€…çš„å…¶ä»–åœ¨çº¿ç”¨æˆ· for (Integer integer : connectionClients.keySet()) { if (!integer.equals(socket.getPort())) { Writer writer = connectionClients.get(integer); writer.write(msg); writer.flush(); } } } // å…¨éƒ¨è½¬å‘ public synchronized void forwardAllMSG(String msg) throws IOException { for (Integer integer : connectionClients.keySet()) { connectionClients.get(integer).write(msg); connectionClients.get(integer).flush(); } } public synchronized void close(){ if(server != null){ try { server.close(); System.out.println("å…³é—­æœåŠ¡å™¨"); } catch (IOException e) { e.printStackTrace(); } } } public void start() { try { // ç»‘å®šç›‘å¬ç«¯å£ server = new ServerSocket(DEFAULT_PORT); System.out.println("[å¯åŠ¨æœåŠ¡å™¨ï¼Œ ç›‘å¬ç«¯å£ " + DEFAULT_PORT + "]"); while (true) { // ç­‰å¾…å®¢æˆ·ç«¯è¿æ¥ Socket socket = server.accept(); // åˆ›å»ºæ–°çš„çº¿ç¨‹, ç”¨äºå¤„ç†å®¢æˆ·ç«¯æ•°æ® new Thread(new ChatHandler(socket, this)).start(); //executor.execute(new ChatHandler(socket, this)); } } catch (IOException e) { e.printStackTrace(); }finally { close(); } } public static void main(String[] args) { ChatServer server = new ChatServer(); server.start(); } } ChatHandlerç”¨äºå¤„ç†å’Œå®¢æˆ·ç«¯äº¤äº’çš„çº¿ç¨‹ public class ChatHandler implements Runnable, Constant { Socket client; ChatServer chatServer; public ChatHandler(Socket socket, ChatServer chatServer){ this.client = socket; this.chatServer = chatServer; } @Override public void run() { try { // å­˜å‚¨æ–°ä¸Šçº¿ç”¨æˆ· chatServer.add(client); BufferedReader reader = new BufferedReader(new InputStreamReader(client.getInputStream())); String msg = null; while ((msg = reader.readLine()) != null){ // è¯»å…¥å®¢æˆ·ç«¯çš„æ¶ˆæ¯ String fwdMSG = "å®¢æˆ·ç«¯ [" + client.getPort() + "] æ¶ˆæ¯ä¸º " + msg + "
"; System.out.print(fwdMSG); // è¯»å‡ºè½¬å‘ç»™å…¶ä»–çš„å®¢æˆ·ç«¯ chatServer.forwardMSG(client, fwdMSG); // æ£€æµ‹æ˜¯å¦é€€å‡º if(QUIT.equalsIgnoreCase(msg)){ break; } } } catch (IOException e) { e.printStackTrace(); }finally { try { chatServer.removeClient(client); } catch (IOException e) { e.printStackTrace(); } } } } å®¢æˆ·ç«¯é™¤äº†å°†æ•°æ®å‘é€ç»™æœåŠ¡ç«¯ä»¥åŠæ¥å—æœåŠ¡ç«¯çš„æ•°æ®,è¿˜éœ€è¦ä¸€ä¸ªæ¥å—ç”¨æˆ·äº§ç”Ÿçš„æ•°æ®çš„çº¿ç¨‹ Clientpublic class ChatClient implements Constant { private Socket socket; private BufferedReader reader; private BufferedWriter writer; // å‘é€ç»™æœåŠ¡å™¨æ•°æ® public void send(String msg) throws IOException { if (!socket.isOutputShutdown()) { writer.write(msg + "
"); writer.flush(); } } // æ¥å—æœåŠ¡å™¨è½¬å‘çš„ä¿¡æ¯ public String receive() throws IOException { String msg = null; if (!socket.isInputShutdown()) { msg = reader.readLine(); } return msg; } // å…³é—­ public void close() { if (writer != null) { try { System.out.println("å®¢æˆ·ç«¯" + socket.getLocalPort() + "å…³é—­"); writer.close(); } catch (IOException e) { e.printStackTrace(); } } } // å‡†å¤‡é€€å‡º public boolean readyQuit(String s) { return s.equalsIgnoreCase(QUIT); } // å¼€å§‹ public void start() { try { socket = new Socket(DEFAULT_HOST, DEFAULT_PORT); writer = new BufferedWriter(new OutputStreamWriter(socket.getOutputStream())); reader = new BufferedReader(new InputStreamReader(socket.getInputStream())); // ç­‰å¾…ç”¨æˆ·è¾“å…¥ä¿¡æ¯ new Thread(new UserInputHandler(this)).start(); // è¯»å–æœåŠ¡å™¨æ•°æ® String msg = null; while ((msg = receive()) != null){ System.out.println(msg); } } catch (IOException e) { e.printStackTrace(); } finally { close(); } } public static void main(String[] args) { ChatClient chatClient = new ChatClient(); chatClient.start(); } } UserInputHandlerpublic class UserInputHandler implements Runnable, Constant { ChatClient chatClient; public UserInputHandler(ChatClient chatClient) { this.chatClient = chatClient; } @Override public void run() { try { // ç­‰å¾…ç”¨æˆ·è¾“å…¥ BufferedReader consoleReader = new BufferedReader( new InputStreamReader(System.in)); while (true) { // è·å–ç”¨æˆ·è¾“å…¥æ•°æ® String input = consoleReader.readLine(); // å‘æœåŠ¡å™¨å‘é€è¾“å…¥æ•°æ® chatClient.send(input); if(chatClient.readyQuit(input)){ break; } } } catch (IOException e) { e.printStackTrace(); } } } æ€»ç»“è¯¥æ¨¡å‹æœ€å¤§çš„é—®é¢˜å°±æ˜¯ç¼ºä¹å¼¹æ€§ä¼¸ç¼©èƒ½åŠ›ï¼Œå½“å®¢æˆ·ç«¯å¹¶å‘è®¿é—®é‡å¢åŠ åï¼ŒæœåŠ¡ç«¯çš„çº¿ç¨‹ä¸ªæ•°å’Œå®¢æˆ·ç«¯å¹¶å‘è®¿é—®æ•°å‘ˆ1:1çš„æ­£æ¯”å…³ç³»ï¼ŒJavaä¸­çš„çº¿ç¨‹ä¹Ÿæ˜¯æ¯”è¾ƒå®è´µçš„ç³»ç»Ÿèµ„æºï¼Œçº¿ç¨‹æ•°é‡å¿«é€Ÿè†¨èƒ€åï¼Œç³»ç»Ÿçš„æ€§èƒ½å°†æ€¥å‰§ä¸‹é™ï¼Œéšç€è®¿é—®é‡çš„ç»§ç»­å¢å¤§ï¼Œç³»ç»Ÿæœ€ç»ˆå°±æ­»æ‰äº†ã€‚ ä¼˜åŒ–2: æœåŠ¡ç«¯ä½¿ç”¨çº¿ç¨‹æ± æ¥å¤„ç†å®¢æˆ·ç«¯çš„è¯·æ±‚ä¸ºäº†æ”¹è¿›è¿™ç§ä¸€è¿æ¥ä¸€çº¿ç¨‹çš„æ¨¡å‹ï¼Œæˆ‘ä»¬å¯ä»¥ä½¿ç”¨çº¿ç¨‹æ± æ¥ç®¡ç†è¿™äº›çº¿ç¨‹ï¼Œå®ç°ä¸€ä¸ªæˆ–å¤šä¸ªçº¿ç¨‹å¤„ç†å¤šä¸ªå®¢æˆ·ç«¯è¯·æ±‚çš„æ¨¡å‹ï¼ˆæœåŠ¡ç«¯çš„çº¿ç¨‹ä¸ªæ•°å’Œå®¢æˆ·ç«¯å¹¶å‘è®¿é—®æ•°å‘ˆM:Nçš„å…³ç³»ï¼ŒNå¯ä»¥è¿œè¿œå¤§äºMï¼Œä½†æ˜¯åº•å±‚è¿˜æ˜¯ä½¿ç”¨çš„åŒæ­¥é˜»å¡I/Oï¼‰ï¼Œé€šå¸¸è¢«ç§°ä¸ºâ€œä¼ªå¼‚æ­¥I/Oæ¨¡å‹â€œã€‚ä¼ªå¼‚æ­¥I/Oæ¨¡å‹å›¾ï¼š å’Œä¼˜åŒ–1ç»å¤§å¤šæ•°éƒ¨åˆ†çš„ä»£ç æ˜¯ç±»ä¼¼çš„,æ‰€ä»¥åªå±•ç¤ºæœåŠ¡ç«¯éœ€è¦ä¿®æ”¹çš„éƒ¨åˆ† private ExecutorService executor; public void start() { try { // ç»‘å®šç›‘å¬ç«¯å£ server = new ServerSocket(DEFAULT_PORT); System.out.println("[å¯åŠ¨æœåŠ¡å™¨ï¼Œ ç›‘å¬ç«¯å£ " + DEFAULT_PORT + "]"); while (true) { // ç­‰å¾…å®¢æˆ·ç«¯è¿æ¥ Socket socket = server.accept(); // åˆ›å»ºæ–°çš„çº¿ç¨‹, ç”¨äºå¤„ç†å®¢æˆ·ç«¯æ•°æ® new Thread(new ChatHandler(socket, this)).start(); executor.execute(new ChatHandler(socket, this)); } } catch (IOException e) { e.printStackTrace(); }finally { close(); } } ä¼˜åŒ–3: NIOæœåŠ¡ç«¯public class ChatServer implements NioConstant { private ServerSocketChannel server; private Selector selector; // è¯»å– buffer private ByteBuffer rBuffer = ByteBuffer.allocate(BUFFER); // å†™å…¥ buffer private ByteBuffer wBuffer = ByteBuffer.allocate(BUFFER); private Charset charset = Charset.forName("UTF-8"); // è‡ªå®šä¹‰ç«¯å£ private int port; public ChatServer(){ this(DEFAULT_PORT); } public ChatServer(int port){ this.port = port; } public void start(){ try { // æ‰“å¼€ä¸€ä¸ªServerSocket çš„ Channel server = ServerSocketChannel.open(); // è®¾ç½®éé˜»å¡ server.configureBlocking(false); // å¾—åˆ°ä¸€ä¸ªå…³äºè¯¥ServerSocketChannelçš„ServerSocketã€å¹¶ä¸”ç»‘å®šç«¯å£ server.socket().bind(new InetSocketAddress(port)); // æ‰“å¼€Selector selector = Selector.open(); // æŠŠServerSocketChannelæ³¨å†Œåˆ°Selector ï¼Œ è®¾ç½®ç›‘å¬äº‹ä»¶ä¸ºACCEPT server.register(selector , SelectionKey.OP_ACCEPT); System.out.println("å¯åŠ¨æœåŠ¡å™¨ï¼Œç›‘å¬ç«¯å£ï¼š"+ port +"..."); while(true){ // æœ¬èº«æ˜¯é˜»å¡å¼è°ƒç”¨ selector.select(); // è§¦å‘äº‹ä»¶é›† Set&lt;SelectionKey&gt; selectionKeys = selector.selectedKeys(); for(SelectionKey key : selectionKeys){ // å¤„ç†è¢«è§¦å‘çš„äº‹ä»¶ handles(key); } // å¤„ç†å®Œæˆåï¼Œæ‰‹åŠ¨æ¸…ç©º selectionKeys.clear(); } } catch (IOException e) { e.printStackTrace(); } finally{ close(selector); } } private boolean readyToQuit(String msg){ return QUIT.equalsIgnoreCase(msg); } private synchronized void close(Closeable closeable){ if(closeable != null){ try { closeable.close(); } catch (IOException e) { e.printStackTrace(); } } } private String receive(SocketChannel client) throws IOException { // å†™æ¨¡å¼ rBuffer.clear(); while((client.read(rBuffer)) &gt; 0); // è¯»æ¨¡å¼ rBuffer.flip(); return String.valueOf(charset.decode(rBuffer)); } private void forwardMessage(SocketChannel client , String fwdMsg) throws IOException { for(SelectionKey key : selector.keys()){ Channel connectedClient = key.channel(); if(connectedClient instanceof ServerSocketChannel){ continue; } if(key.isValid() &amp;&amp; !client.equals(connectedClient)){ // å†™æ¨¡å¼ wBuffer.clear(); wBuffer.put(charset.encode(getClientName(client) +":"+fwdMsg)); // è¯»æ¨¡å¼ wBuffer.flip(); while(wBuffer.hasRemaining()){ ((SocketChannel) connectedClient).write(wBuffer); } } } } private String getClientName(SocketChannel client){ return "å®¢æˆ·ç«¯["+client.socket().getPort()+"]"; } private void handles(SelectionKey key) throws IOException { // ACCEPTäº‹ä»¶ - å’Œå®¢æˆ·ç«¯å»ºç«‹äº†è¿æ¥ if(key.isAcceptable()){ ServerSocketChannel server = (ServerSocketChannel) key.channel(); SocketChannel client = server.accept(); // è®¾ç½®æˆéé˜»å¡ client.configureBlocking(false); client.register(selector , SelectionKey.OP_READ); System.out.println(getClientName(client)+"å·²è¿æ¥"); } // READäº‹ä»¶ - å®¢æˆ·ç«¯å‘é€äº†æ¶ˆæ¯ else if(key.isReadable()){ SocketChannel client = (SocketChannel) key.channel(); String fwdMsg = receive(client); if(fwdMsg.isEmpty()){ // å®¢æˆ·ç«¯å¼‚å¸¸ , ä¸å†ç›‘å¬è¿™ä¸ªäº‹ä»¶ key.cancel(); // æ›´æ–°ç›‘å¬äº‹ä»¶çŠ¶æ€ selector.wakeup(); } else{ forwardMessage(client , fwdMsg); // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å‡†å¤‡é€€å‡º if(readyToQuit(fwdMsg)){ key.cancel(); selector.wakeup(); System.out.println(getClientName(client)+"å·²æ–­å¼€"); } } } } public static void main(String[] args) { ChatServer server = new ChatServer(); server.start(); } } å®¢æˆ·ç«¯public class ChatClient implements NioConstant { private String host; private int port; private SocketChannel client; private ByteBuffer rBuffer = ByteBuffer.allocate(BUFFER); private ByteBuffer wBuffer = ByteBuffer.allocate(BUFFER); private Selector selector; private Charset charset = Charset.forName("UTF-8"); public ChatClient(){ this(DEFAULT_HOST , DEFAULT_PORT); } public ChatClient(String host , int port){ this.host = host; this.port = port; } // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å‡†å¤‡é€€å‡º public boolean readyQuit(String msg){ return QUIT.equalsIgnoreCase(msg); } public void close(Closeable closeable){ if(closeable != null){ try { closeable.close(); } catch (IOException e) { e.printStackTrace(); } } } public void start(){ try { selector = Selector.open(); client = SocketChannel.open(); // è®¾ç½®éé˜»å¡ client.configureBlocking(false); client.register(selector , SelectionKey.OP_CONNECT); client.connect(new InetSocketAddress(host , port)); while(true){ selector.select(); Set&lt;SelectionKey&gt; selectionKeys = selector.selectedKeys(); for(SelectionKey key : selectionKeys){ handles(key); } } } catch (IOException e) { e.printStackTrace(); } catch (ClosedSelectorException e){ // ç”¨æˆ·æ­£å¸¸é€€å‡ºï¼Œäº§ç”Ÿçš„å¼‚å¸¸ } finally { close(selector); } } private void handles(SelectionKey key) throws IOException { // CONNECTäº‹ä»¶ - è¿æ¥å°±ç»ªäº‹ä»¶ if(key.isConnectable()){ SocketChannel client = (SocketChannel) key.channel(); if(client.isConnectionPending()){ client.finishConnect(); // å¤„ç†ç”¨æˆ·çš„è¾“å…¥ new Thread(new UserInputHandler(this)).start(); } client.register(selector , SelectionKey.OP_READ); } // READäº‹ä»¶ - æœåŠ¡å™¨è½¬å‘æ¶ˆæ¯ else if(key.isReadable()){ SocketChannel client = (SocketChannel) key.channel(); String msg = receive(client); if(msg.isEmpty()){ // æœåŠ¡å™¨å¼‚å¸¸ close(selector); } else{ System.out.println(msg); } } } private String receive(SocketChannel client) throws IOException { // å†™æ¨¡å¼ rBuffer.clear(); while(client.read(rBuffer) &gt; 0); // å†™æ¨¡å¼ rBuffer.flip(); return String.valueOf(charset.decode(rBuffer)); } public void send(String msg) throws IOException { if(msg.isEmpty()){ return ; } // å†™æ¨¡å¼ wBuffer.clear(); wBuffer.put(charset.encode(msg)); wBuffer.flip(); while(wBuffer.hasRemaining()){ client.write(wBuffer); } // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å‡†å¤‡é€€å‡º if(readyQuit(msg)){ close(selector); } } public static void main(String[] args) { ChatClient client = new ChatClient(); client.start(); } }

